package section3JavaGettingStarted;

public class PrimitiveDatatypes_booleanandChar_16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean var = true;
		//true is a keyword 
		//boolean value can either contain true or false and it cannot contain any other value
		System.out.println(var);
		
		char var1 = '&';
		//char should be in single quotes
		System.out.println(var1);
		
		//unicode coding mechanism where we can print any character by using the unicode and print in Java
		//https://unicode-table.com/en/
		
		char unicode = '\u00A7';
		//uncidoe should be given with a back slash followed by the unicode
		System.out.println(unicode);

	}

}
