package section5JavaDecision;

public class ArithematicOperators_33 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 1+2;
		int y = 12-x;
		String x1 = "Hello" + "World";
		//modulous operator % is used for remainder
		
		int a = 14%5;
		
		//String performs arithmatic and concatinate operations
		
		System.out.println(x);
		System.out.println(x1);
		System.out.println(y);
		System.out.println(a);
		
		//for increment or decrement operator 
		int b=20;
		System.out.println(++b);
		//the value will be assigned to the increment and now it remains to the position as 20
		System.out.println(--b);
		

	}

}
