package section5JavaDecision;

public class TernaryOperator_37 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// when an operator operators on 2 operators are called as Binary operation
		int x= 5;
		//Unary operator
		boolean y = !true;
		
		//Ternary operator and there is only one operator in Java
		
		boolean a = (5>4)?true:false;
		System.out.println(a);
		System.out.println(y);
		
		int z;
		z=(5==6)? 1:1;
		System.out.println(z);
		

	}

}
